import React from "react";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { settings } from "config/C4";
import {
  faJsSquare,
  faReact,
  faSass,
  faNodeJs,
  faFigma,
} from "@fortawesome/free-brands-svg-icons";
import AdobeXD from "../../assets/images/adobexd.svg";
import Illustrator from "../../assets/images/illustrator.svg";
import Photoshop from "../../assets/images/photoshop.svg";

const OverViewSkills = () => {
  const { skills } = settings;

  const Icon = ({icon}) => {
    if (icon === "js") {
      return faJsSquare;
    } else if (icon === "react") {
      return <FontAwesomeIcon icon={faReact} />;
    } else if (icon === "sass") {
      return <FontAwesomeIcon icon={faSass} />;
    } else if (icon === "node") {
      return <FontAwesomeIcon icon={faNodeJs} />;
    } else if (icon === "illustrator") {
      return <img src={Illustrator} alt="" />;
    } else if (icon === "photoshop") {
      return <img src={Photoshop} alt="" />;
    } else if (icon === "figma") {
      return <FontAwesomeIcon icon={faFigma} />;
    } else if (icon === "adobexd") {
      return <img src={AdobeXD} alt="" />;
    }
  };

  const skillCircles = ({level}) => {
    // const emptyCircles = 5 - level;

    const allCircles = [];
    for(let i; i <= level; i++) {
      allCircles.push('fullCircle');
    }
    for(let i; i <= 5 - level; i++) {
      allCircles.push('emptyCircle');
    }

    return allCircles;
  }
  return (
    <div className="overView__skills">
      {skills &&
        skills
          .filter((s) => {
            return s.overview === true;
          })
          .map((skill, i) => {
            return (
              <div key={i} className="overView__skills--item">
                <div className="icon">
                  <Icon icon={skill.icon} />
                </div>
                <div className="skillCircles">
                  {skillCircles.map((c, i) => {
                    return <div key={i} className={`${c}`} />
                  })}
                </div>
              </div>
            );
          })}
    </div>
  );
};

export default OverViewSkills;
