import React from "react";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { settings } from "config/C4";
import {
  faJsSquare,
  faReact,
  faSass,
  faNodeJs,
  faFigma,
} from "@fortawesome/free-brands-svg-icons";
import AdobeXD from "../../assets/images/adobexd.svg";
import Illustrator from "../../assets/images/illustrator.svg";
import Photoshop from "../../assets/images/photoshop.svg";

const setIcon = (icon) => {
    if (icon === "js") {
      return faJsSquare;
    } else if (icon === "react") {
      return faReact;
    } else if (icon === "sass") {
      return faSass;
    } else if (icon === "node") {
      return faNodeJs;
    } else if (icon === "illustrator") {
      return Illustrator;
    } else if (icon === "photoshop") {
      return Photoshop;
    } else if (icon === "figma") {
      return faFigma;
    } else if (icon === "adobexd") {
      return AdobeXD;
    } else {
      return null;
    }
  };

const OverViewSkills = () => {
  const { skills } = settings;

  const skillCircles = (level) => {
    // const emptyCircles = 5 - level;

    const allCircles = [];
    for (let i; i <= level; i++) {
      allCircles.push("fullCircle");
      console.log(allCircles)
    }
    for (let i; i <= 5 - level; i++) {
      allCircles.push("emptyCircle");
    }

    console.log(typeof level, 'level');

    return allCircles;
  };

  return (
    <div className="overView__skills">
      {skills &&
        skills
          .filter((s) => {
            return s.overview === true;
          })
          .map((skill, i) => {
            const Icon = () => {
                if (skill.fontawesome) {
                  return (
                    <FontAwesomeIcon
                      icon={setIcon(skill.icon)}
                      className="skillWidget__icon"
                    />
                  );
                } else {
                  return (
                    <img
                      src={setIcon(skill.icon)}
                      alt={skill.name}
                      className="skillWidget__icon"
                    />
                  );
                }
              };
            return (
              <div key={i} className="overView__skills--item">
                <div className="icon">
                  <Icon />
                </div>
                <div className="skillCircles">
                  {skillCircles(skill.level).map((c, i) => {
                      console.log(c);
                    return <div key={i} className={`${c}`} />
                  })}
                </div>
              </div>
            );
          })}
    </div>
  );
};

export default OverViewSkills;
