import React, { Component } from "react";
import { VictoryChart, VictoryLine } from "victory";
import { ContentHeader, settings } from "config/C4";

class OverViewContributions extends Component {
  constructor() {
    super();
    this.state = {
      contributions: {},
      chartData: [],
    };
  }

  async componentDidMount() {
    await this.getContributions();
    this.setContributionData();
  }

  async getContributions() {
    const { accessToken } = settings;
    const headers = {
      Authorization: `bearer ${accessToken}`,
    };
    const body = {
      query: `query {
                user(login: "JannekeCoumans") {
                  name
                  contributionsCollection {
                    contributionCalendar {
                      totalContributions
                      weeks {
                        contributionDays {
                          contributionCount
                        }
                        firstDay
                      }
                    }
                  }
                }
              }`,
    };
    const response = await fetch("https://api.github.com/graphql", {
      method: "POST",
      body: JSON.stringify(body),
      headers: headers,
    });
    const result = await response.json();
    if (!result.errors && result.data) {
      this.setState({ contributions: result.data });
    }
  }

  setContributionData() {
    const { contributions } = this.state;
    let data = [];
    const months = {
      1: {contributions: null, nameOfMonth: 'January'},
      2: {contributions: null, nameOfMonth: 'Februari'},
      3: {contributions: null, nameOfMonth: 'March'},
      4: {contributions: null, nameOfMonth: 'April'},
      5: {contributions: null, nameOfMonth: 'May'},
      6: {contributions: null, nameOfMonth: 'June'},
      7: {contributions: null, nameOfMonth: 'July'},
      8: {contributions: null, nameOfMonth: 'August'},
      9: {contributions: null, nameOfMonth: 'September'},
      10: {contributions: null, nameOfMonth: 'October'},
      11: {contributions: null, nameOfMonth: 'November'},
      12: {contributions: null, nameOfMonth: 'December'},
    } 

    if (contributions && contributions.user) {
      const contributionWeeks = Object.values(
        contributions.user.contributionsCollection.contributionCalendar.weeks
      );
      data = contributionWeeks.map((week) => {
        let weeklyContributions = null;
        week.contributionDays.map(
          (item) =>
            (weeklyContributions = weeklyContributions + item.contributionCount)
        );
        const date = new Date(week.firstDay);
        const month = date.getMonth() + 1;
        console.log(month);
        return { x: week.firstDay, y: weeklyContributions };
      });
    }
    this.setState({
      chartData: data,
    })
  }

  render() {
    const { chartData } = this.state;
    return (
      <div className="overViewContributions">
        <ContentHeader title="github contributions" />
        <VictoryChart>
          <VictoryLine
            style={{
              data: { stroke: "#c43a31" },
              parent: { border: "1px solid #ccc"}
            }}
            data={chartData}
          />
        </VictoryChart>
      </div>
    );
  }
}
export default OverViewContributions;
