import React from "react";
import { settings } from "config/C4";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { PieChart } from "react-minimal-pie-chart";

import {
  faJsSquare,
  faReact,
  faSass,
  faNodeJs,
  faFigma,
} from "@fortawesome/free-brands-svg-icons";
import AdobeXD from "../../assets/images/adobexd.svg";
import Illustrator from "../../assets/images/illustrator.svg";
import Photoshop from "../../assets/images/photoshop.svg";

const SkillWidget = () => {
  const { skills } = settings;

  const setIcon = (icon) => {
    if (icon === "js") {
      return faJsSquare;
    } else if (icon === "react") {
      return faReact;
    } else if (icon === "sass") {
      return faSass;
    } else if (icon === "node") {
      return faNodeJs;
    } else if (icon === "illustrator") {
      return Illustrator;
    } else if (icon === "photoshop") {
      return Photoshop;
    } else if (icon === "figma") {
      return faFigma;
    } else if (icon === "adobexd") {
      return AdobeXD;
    }
  };

  //   const pieCharts = skills.map((skill, i) => {
  //     const Icon = skill.fontawesome ? (
  //       <FontAwesomeIcon icon={setIcon(skill.icon)} />
  //     ) : (
  //       <img src={skill.icon} alt={skill.name} />
  //     );

  //     return (
  //       <PieChart
  //         data={[{ value: skill.level }]}
  //         totalValue={5}
  //         label={() => <Icon />}
  //       />
  //     );
  //   });

  return (
    <div className="skillWidget">
      {skills &&
        skills.map((skill, i) => {
          const Icon = () => {
            if (skill.fontawesome) {
              return (
                <FontAwesomeIcon
                  icon={setIcon(skill.icon)}
                  className="skillWidget__icon"
                />
              );
            } else {
              return (
                <img
                  src={setIcon(skill.icon)}
                  alt={skill.name}
                  className="skillWidget__icon"
                />
              );
            }
          };
          if (skill.widget) {
            return (
              <div className="skillWidget__item">
                <PieChart
                  key={i}
                  data={[{ value: skill.level, color: "#23695D" }]}
                  totalValue={5}
                  lineWidth={15}
                  startAngle={200}
                />
                <Icon />
              </div>
            );
          }

          return null;
        })}
    </div>
  );
};

export default SkillWidget;
