import { StorageHandler } from 'config/C4';

const EN = {
  global: {
    nav: {
      overview: 'Overview',
      personal: 'Personal Info',
      skillset: 'Skillset',
      work: 'Work Experience',

    }
  },
  widgets: {
    skills: 'Skills',
    skillsCopy: 'Always actively skillbuilding',
    socialLinks: 'Social Links',
    socialLinksCopy: 'Some of my professional profiles',
    contact: {
      first: 'Want to get in touch with me? ',
      span: 'I\'d love that! ',
      last: 'Send me an email and I will get back to you ASAP.'
    }
  },
  overview: {
    titles: {
      skills: 'Skills',
      jobs: 'Current & Previous jobs',
      portfolio: 'Portfolio Items',
    },
    facts: {
      title: 'A few quick facts',
      firstName: 'First Name',
      lastName: 'Last Name',
      city: 'Place of Residence',
      birthday: 'Date of Birth',
    },
  },
};

const NL = {
  global: {
    nav: {
      overview: 'Overzicht',
      personal: 'Persoonlijk',
      skillset: 'Vaardigheden',
      work: 'Werkervaring',
    }
  },
  widgets: {
    skills: 'Vaardigheden',
    skillsCopy: 'Altijd bezig met nieuwe dingen leren',
    socialLinks: 'Social Links',
    socialLinksCopy: 'Een paar van mijn professionele profielen',
    contact: {
      first: 'Wil je in contact komen met mij? ',
      span: 'Dat vind ik superleuk! ',
      last: 'Stuur me een e-mail en dan neem ik zsm contact met je op.'
    }
  },
  overview: {
    titles: {
      skills: 'Vaardigheden',
      jobs: 'Huidige & Vorige werkgevers',
      portfolio: 'Portfolio Items',
    },
    facts: {
      title: 'Een paar snelle feitjes',
      firstName: 'Voornaam',
      lastName: 'Achternaam',
      city: 'Woonplaats',
      birthday: 'Geboortedatum',
    },
  },
};

let languageFileToExport = NL;
const setLanguage = StorageHandler.get('language');
if (setLanguage && setLanguage.code === 'EN') {
  languageFileToExport = EN;
}

export default languageFileToExport;
