import React, { useEffect, useState } from 'react';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faChevronDown, faGlobe } from '@fortawesome/free-solid-svg-icons';

import { StorageHandler } from 'config/C4';

const LanguageSelect = () => {
  const [activeLanguage, setActiveLanguage] = useState(StorageHandler.get('language') || {code: 'NL', name: 'Nederlands'});
  const [languageSelectIsActive, setLanguageSelectIsActive] = useState(false);

  const checkLanguage = () => {
    const language = StorageHandler.get('language');
    if (!language) {
      const languageObject = {
        code: 'NL',
        name: 'Nederlands'
      }
      setActiveLanguage(languageObject);
      StorageHandler.set('language', languageObject);
    }
  }

  useEffect(() => {
    checkLanguage();
  })

  const toggleLanguageSelect = () => {
    setLanguageSelectIsActive(!languageSelectIsActive);
  }

  return (
    <>
    <button type="button" className="languageSelect" onClick={toggleLanguageSelect}>
      <FontAwesomeIcon icon={faGlobe} />
      <p>
        <span>{activeLanguage.name}</span>
        <FontAwesomeIcon icon={faChevronDown} />
      </p>
    </button>
    {languageSelectIsActive && <h1>Hoiiiii</h1>}
    </>
  )
}

export default LanguageSelect;
