import React, { Component } from "react";
import { settings } from "config/C4";

class OverViewContributions extends Component {
  constructor() {
    super();
    this.state = {
      contributions: {},
    };
  }

  componentDidMount() {
    this.getContributions();
  }

  async getContributions() {
    const { accessToken } = settings;
    const headers = {
      Authorization: `bearer ${accessToken}`,
    };
    const body = {
      query: `query {
                user(login: "JannekeCoumans") {
                  name
                  contributionsCollection {
                    contributionCalendar {
                      totalContributions
                      weeks {
                        contributionDays {
                          contributionCount
                        }
                        firstDay
                      }
                    }
                  }
                }
              }`,
    };
    const response = await fetch("https://api.github.com/graphql", {
      method: "POST",
      body: JSON.stringify(body),
      headers: headers,
    });
    const result = await response.json();
    if (!result.errors && result.data) {
      this.setState({ contributions: result.data });
    }
  }

  setContributionData() {
    const { contributions } = this.state;
    const data = [];

    if (contributions && contributions.user) {
        const contributionWeeks = Object.values(
          contributions.user.contributionsCollection.contributionCalendar.weeks
        );
        const weekly = contributionWeeks.map((week) => {
          let weeklyContributions = null;
          week.contributionDays.map((item) => weeklyContributions = weeklyContributions + item.contributionCount);
          console.log({week: week.firstDay, contributions: weeklyContributions});
          return {week: week.firstDay, contributions: weeklyContributions};
        });
        // console.log(weekly, 'weekly');
      }
  }

  render() {
    return <h1>OverViewContributions</h1>;
  }
}
export default OverViewContributions;
