import React, { useEffect, useState } from "react";
import { Link } from "react-router-dom";
import { ContentHeader, PortfolioData } from "config/C4";

const PortfolioItemView = () => {
  const [portfolioItem, setPortfolioItem] = useState(null);

  const getPortfolioItem = () => {
    const slug = window.location.pathname.split("/portfolio-item/")[1];
    const filteredPortfolioItem = PortfolioData.filter(
      (item) => item.slug === slug
    )[0];
    if (!portfolioItem) {
      setPortfolioItem(filteredPortfolioItem);
    }
  };

  useEffect(() => {
    getPortfolioItem();
  });

  return (
    <div className="portfolioItemView">
      {portfolioItem ? (
        <>
          <div
            className="portfolioItemView__header"
            style={{
              backgroundImage: `url(${
                require(`../../assets/images/portfolio/${portfolioItem.slug}-featured.jpg`)
                  .default
              })`,
            }}
          >
            <ContentHeader title={portfolioItem.name} />
          </div>
          <div className="portfolioItemView__badges">
            {portfolioItem.buildWith.map((item, i) => (
              <div className="badge" key={i}>{item}</div>
            ))}
          </div>
        </>
      ) : (
        <>
          <h1>Oeps er is iets verkeerd gegaan. Probeer opnieuw.</h1>
          <Link to="/portfolio" className="btn">
            Terug naar porfolio
          </Link>
        </>
      )}
    </div>
  );
};

export default PortfolioItemView;
