import React, { useEffect, useState } from 'react';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faChevronDown, faGlobe } from '@fortawesome/free-solid-svg-icons';

import { StorageHandler } from 'config/C4';

const LanguageSelect = () => {
  const [activeLanguage, setActiveLanguage] = useState(StorageHandler.get('language') || {code: 'NL', name: 'Nederlands'});
  const [languageSelectIsActive, setLanguageSelectIsActive] = useState(false);

  const checkLanguage = () => {
    const language = StorageHandler.get('language');
    if (!language) {
      const languageObject = {
        code: 'NL',
        name: 'Nederlands'
      }
      setActiveLanguage(languageObject);
      StorageHandler.set('language', languageObject);
    }
  }

  useEffect(() => {
    checkLanguage();
  })

  const changeLanguage = (e) => {
    const { value } = e.target;
    if (value === 'NL') {
      setActiveLanguage({ code: 'NL', name: 'Nederlands' });
    } else if (value === 'EN') {
      setActiveLanguage({ code: 'EN', name: 'English' });
    }
  }

  return (
    <>
    <button type="button" className="languageSelect" onClick={() => setLanguageSelectIsActive(!languageSelectIsActive)}>
      <FontAwesomeIcon icon={faGlobe} />
      <p>
        <span>{activeLanguage.name}</span>
        <FontAwesomeIcon icon={faChevronDown} />
      </p>
    </button>
    {languageSelectIsActive && (
      <div className="languageSelect__menu">
        <button type="button" onClick={changeLanguage} value="NL" className={activeLanguage.code === 'NL'? 'active' : ''}>Nederlands</button>
        <button type="button" onClick={changeLanguage} value="EN" className={activeLanguage.code === 'EN'? 'active' : ''}>English</button>
      </div>
    )}
    </>
  )
}

export default LanguageSelect;
